export interface INodeComponent {
    type: string;
    className: string;
    stroke?: string;
    "stroke-width"?: number;
}
export interface INodeComponentRect extends INodeComponent {
    type: 'rect';
    width: number;
    height: number;
    fill: string;
    rx?: number;
    x?: number;
    y?: number;
}
export interface INodeComponentText extends INodeComponent {
    type: 'text';
    'font-size'?: number;
    'font-family'?: string;
    refField: string;
    x?: number;
    y?: number;
}
